mapper.select:

		
		select count(*) 
		from category 
		where id = #{id }


		
		select no, name, description 
		from category 
		where id = #{id };

		
				
				select a.no,a.name,a.description,a.reg_date,a.id,count(b.category_no) as numberOfPost 
         		from category a left outer join post b on a.no=b.category_no 
         		where id=#{id}
         		group by a.no;


select min(no)as 'first_category' 
from category
where id = #{id };
